mapper.update:

update order_shipping_details_shipment_tracking_details
set user_id = #{user_id,jdbcType=INTEGER},
  shop_id = #{shop_id,jdbcType=INTEGER},
  shipment_tracking_number = #{shipment_tracking_number,jdbcType=VARCHAR},
  shipping_carrier_used = #{shipping_carrier_used,jdbcType=VARCHAR}
where order_id = #{order_id,jdbcType=VARCHAR}
  and order_shipping_details_shipment_tracking_details_id = #{order_shipping_details_shipment_tracking_details_id,jdbcType=INTEGER}

mapper.select:

select order_id, order_shipping_details_shipment_tracking_details_id, user_id, shop_id, 
shipment_tracking_number, shipping_carrier_used
from order_shipping_details_shipment_tracking_details
where order_id = #{order_id,jdbcType=VARCHAR}
  and order_shipping_details_shipment_tracking_details_id = #{order_shipping_details_shipment_tracking_details_id,jdbcType=INTEGER}
