mapper.delete:

delete from order_transaction_item
where transaction_id = #{transaction_id,jdbcType=VARCHAR}
  and item_id = #{item_id,jdbcType=VARCHAR}

update.foreach:
update order_transaction_item
order_id = #{item.order_id,jdbcType=VARCHAR}, user_id = #{item.user_id,jdbcType=INTEGER}, shop_id = #{item.shop_id,jdbcType=INTEGER}, attribute_array = #{item.attribute_array,jdbcType=VARCHAR}, integrated_merchant_credit_card_enabled = #{item.integrated_merchant_credit_card_enabled,jdbcType=TINYINT}, private_notes = #{item.private_notes,jdbcType=VARCHAR}, seller_inventory_id = #{item.seller_inventory_id,jdbcType=VARCHAR}, site = #{item.site,jdbcType=VARCHAR}, SKU = #{item.SKU,jdbcType=VARCHAR}, title = #{item.title,jdbcType=VARCHAR}
where transaction_id = #{item.transaction_id,jdbcType=VARCHAR} and item_id = #{item_id,jdbcType=VARCHAR}

mapper.select:

select transaction_id, item_id, order_id, user_id, shop_id, attribute_array, integrated_merchant_credit_card_enabled, 
private_notes, seller_inventory_id, site, SKU, title
from order_transaction_item
where transaction_id = #{transaction_id,jdbcType=VARCHAR}
  and item_id = #{item_id,jdbcType=VARCHAR}
